```typescript
// instance.ts

function createInstance(isPrivate: boolean): GameInstance {
  const instance: GameInstance = {
    id: generateId(),
    code: isPrivate ? generateRoomCode() : undefined,
    phase: 'lobby',
    players: new Set(),
    submissions: new Map(),
